changeSet.sql:
create table ${database.defaultSchemaName}.knowledgebasearticlelinkage
(
    id uuid unique not null,
    articleId varchar(512) not null,
    providerType varchar(512) not null,
    objectType varchar(512) not null,
    objectId varchar(512) not null,
    primary key (id)
);

CREATE INDEX objectTypeToId_index ON ${database.defaultSchemaName}.knowledgebasearticlelinkage (objectType, objectId);
CREATE INDEX articleId_index ON ${database.defaultSchemaName}.knowledgebasearticlelinkage (articleId);
CREATE INDEX providerTypeToArticleId_index ON ${database.defaultSchemaName}.knowledgebasearticlelinkage (providerType, articleId);

alter table if exists ${database.defaultSchemaName}.knowledgebasearticlelinkage
    add constraint record_unique_constraint unique (providerType, articleId, objectType, objectId);
