create table ${database.defaultSchemaName}.KnowledgeBaseProviderConfiguration ( id uuid not null, lastChanged timestamp,
providerType uuid not null, isActive boolean, adapterAuthType varchar(255), adapterURL varchar(512), adapterCredential jsonb, siteAuthType varchar(255), siteURL varchar(512), siteCredential jsonb, primary key (id));
